// To try the methods of the File class package forFilesAndStreams;import java.io.*;import java.awt.*;import java.awt.event.*;import javax.swing.*;public class TryFileClass extends JFrame implements ActionListener{	private JLabel promptL, resultsL;	private JPanel inputP, outputP;	private JTextField inputTF;	private JScrollPane outputSP;	private JTextArea outputTA;	private JButton testB;	private Container myCP;		public TryFileClass() {		super("Try File Class Methods");		setSize(400,300);		setLocation(100,100);		myCP = getContentPane();				inputP = new JPanel(new GridLayout(2,1));		myCP.add(inputP,BorderLayout.NORTH);				promptL = new JLabel("Enter the File name here:");		inputP.add(promptL);		inputTF = new JTextField(30);		inputP.add(inputTF);				testB = new JButton ("Test");		myCP.add(testB,BorderLayout.WEST);		testB.addActionListener(this);				outputP = new JPanel(new GridLayout(2,1));		myCP.add(outputP,BorderLayout.CENTER);				resultsL = new JLabel("Test Results", JLabel.CENTER);		outputP.add(resultsL);		outputTA = new JTextArea();		outputSP = new JScrollPane(outputTA,						JScrollPane.VERTICAL_SCROLLBAR_ALWAYS,						JScrollPane.HORIZONTAL_SCROLLBAR_NEVER);		outputSP.setPreferredSize(new Dimension(200,100));		outputP.add(outputSP);  		addWindowListener(new WindowAdapter() {			public void windowClosing(WindowEvent e) {				System.exit(0);			}//windowClosing		}); //end of definition of WindowAdapter and semicolon to end the line				setVisible(true); 	}//constructor 		public void actionPerformed(ActionEvent e) {		String userInput = inputTF.getText();		File dataFile = new File (userInput);				if(dataFile.exists()) {			outputTA.setText( dataFile.getName() + " exists\n" +				(dataFile.isFile() ? "is a file\n"								:"is not a file\n") +				(dataFile.canRead() ? "can be read\n"								:"cannot be read\n") +				(dataFile.canWrite() ? "can be written\n"								:"cannot be written\n"));				}else {			outputTA.setText (userInput + " does not exist");		}//else		inputTF.setText("");		inputTF.requestFocus();	}//actionPerformed		public static void main (String args[]) {		TryFileClass myApplication = new TryFileClass();	}//main method	}//class TryFileClass					