package forLinkedListLab2;//For CS 64 Lab 08//File: Node.java/*A class for Nodes used within a WCSLinkedList. Contains two constructors,access, and modifier methods.*/public class Node<E> {	private E myData;	private Node<E> myLink;		public Node (E theData, Node<E> theLink) {		myData = theData;		myLink = theLink;	} //two parameter constructor		public Node (E theData) {		myData = theData;		myLink = null;	} //one parameter constructor		public void setLink(Node<E> theLink) {		myLink = theLink;	}  //setLink		public void setData(E theData) {		myData = theData;	}  //getData	public Node<E> getLink(){		return myLink;	}  //getLink		public E getData() {		return myData;	}  //getData	}//Node class